import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

const validationSchema = Yup.object({
  name: Yup.string().required('Le nom est requis'),
  email: Yup.string().email('Email invalide').required('L\'email est requis'),
  date: Yup.string().required('La date est requise'),
  file: Yup.mixed().required('Le fichier est requis'),
});

function App() {
  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      date: '',
      file: null,
    },
    validationSchema,
    onSubmit: async (values) => {
      console.log(values)
      const formData = new FormData();
      formData.append('name', values.name);
      formData.append('email', values.email);
      formData.append('date', values.date);
      formData.append('file', values.file);
      for (let pair of formData.entries()) {
        console.log(pair[0] + ':', pair[1]);
      }
      // Envoyer à l'API
      console.log(formData)
      let res = await fetch('https://9793-85-169-182-214.ngrok-free.app/addUser', {
        method: 'POST',
        body: JSON.stringify({
          name : values.name,
          email : values.email,
        }),
        headers: {
          'ngrok-skip-browser-warning': 'true',
          'Content-Type': 'application/json',
        },
      }).then((response) => response.json());
      console.log("res => ", res)
    },
  });

  return (
    <div className="App">
      <h1>Formulaire de Rendez-vous</h1>
      <form onSubmit={formik.handleSubmit}>
        <div>
          <label htmlFor="name">Nom :</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formik.values.name}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
          />
          {formik.touched.name && formik.errors.name && <div>{formik.errors.name}</div>}
        </div>
        <div>
          <label htmlFor="email">Email :</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formik.values.email}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
          />
          {formik.touched.email && formik.errors.email && <div>{formik.errors.email}</div>}
        </div>
        <div>
          <label htmlFor="date">Date du Rendez-vous :</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formik.values.date}
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
          />
          {formik.touched.date && formik.errors.date && <div>{formik.errors.date}</div>}
        </div>
        <div>
          <label htmlFor="file">Fichier :</label>
          <input
            type="file"
            id="file"
            name="file"
            onChange={(event) => {
              formik.setFieldValue('file', event.currentTarget.files[0]);
            }}
          />
          {formik.touched.file && formik.errors.file && <div>{formik.errors.file}</div>}
        </div>
        <div>
          <button type="submit">Soumettre</button>
        </div>
      </form>
    </div>
  );
}

export default App;
